import { cn } from "@/lib/utils";

export function FieldContainer({
  children,
  className,
}: {
  children: React.ReactNode;
  className?: string;
}) {
  return <div className={cn("space-y-1", className)}>{children}</div>;
}

interface FieldErrorProps {
  error?: string;
}
export function FieldError({ error }: FieldErrorProps) {
  if (!error) return null;

  return <p className='text-sm text-red-600 dark:text-red-400'>{error}</p>;
}

import { Label } from "@/registry/new-york/ui/label";
import { cn } from "@/lib/utils";
import { RequiredFieldIcon } from "@/registry/new-york/components/required-field-icon";

export interface FieldLabelProps {
  required?: boolean;
  label?: string;
  disabled?: boolean;
  id?: string;
  className?: string;
}

export function FieldLabel({
  required,
  label,
  disabled,
  id,
  className,
}: FieldLabelProps) {
  if (!label) return null;

  return (
    <Label
      asChild={id === undefined}
      aria-disabled={disabled}
      className={cn(
        "flex gap-x-0.5",
        {
          "text-muted-foreground/80": disabled,
        },
        className
      )}
      htmlFor={id}
    >
      {id === undefined ? (
        <div>
          <span>{label}</span>
          {required && <RequiredFieldIcon className='self-start' />}
        </div>
      ) : (
        <>
          <span>{label}</span>
          {required && <RequiredFieldIcon className='self-start' />}
        </>
      )}
    </Label>
  );
}

import { cn } from "@/lib/utils";
import { AsteriskIcon } from "lucide-react";

export function RequiredFieldIcon({ className }: { className?: string }) {
  return (
    <AsteriskIcon
      className={cn("size-3 text-red-600 dark:text-red-400", className)}
    />
  );
}
